mapper.select:
select ac.id as id, ac.article_id as articleId, ac.comment_content as commentContent, ac.comment_praise_count as commentPraiseCount, ac.comment_reply_count as commentReplyCount, ac.create_user as createUser, u.user_name as createUserName, f.file_path as userIconPath, ac.create_time as createTime from article_comment as ac left join user as u on u.id = ac.create_user left join files as f on f.id = u.user_icon_id where ac.status="1" and article_id=#{articleId}
order by comment_praise_count desc
order by ac.create_time desc
order by ac.create_time asc
limit ${limit},${offset}

mapper.update:
update article_comment 
set

	comment_praise_count = comment_praise_count + #{praiseCountAddValue}


	comment_reply_count = comment_reply_count + #{replyCountAddValue}

where id = #{commentId};
